//ペット情報変更
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './pet_change.module.css';
import fontstyles from '../font/font.module.css';
import Left1Img from '../image/Left1.png'; //259:550
import Right1Img from '../image/Right1.png'; //259:750

const PetChange = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [comment, setComment] = useState('');
  const [imagePreview, setImagePreview] = useState(null);

  const handleTop = () => {
    navigate('/top');
  };

  const handlepetcon = async() => {
    try {
      const response = await fetch('http://localhost:8080/userinfo/info/pet', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userid: document.cookie, name, comment}),
      });

      const data = await response.json();

      if (response.ok) {
        if (data !== -1) {  // 認証成功
          navigate('/pet_con');
        }
      };
    } catch (error) {
    };
  };
  const inputStyle = {
    fontFamily: 'CraftMincho, serif'
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className={fontstyles.fontFamily}>
    <div className={styles.body}>
      <div className={styles.left}>
        <button
          className={styles['main-btn']}
          onClick={handleTop}
          style={inputStyle}
        >
          トップページへ戻る
        </button>
        <div className={styles.advertisement}>
          <img
            src={Left1Img} // 広告サンプル
            alt="Left1Img" // 代替テキスト
          />  
        </div>
      </div>

      <div className={styles.center}>
        <div className={styles.pet}>ペット情報変更</div>
        <div className={styles.white}>
          <div className={styles.photoContainer}>
            <div className={styles.photo}>
              {imagePreview ? (
                <img
                  src={imagePreview}
                  alt="ペットの画像"
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    borderRadius: '50%'
                  }}
                />
              ) : (
                <div>🐾</div>
              )}
            </div>
            <button
              className={styles.imageButton}
              style={inputStyle}
              onClick={() => document.getElementById('imageInput').click()}
            >
              画像を挿入
            </button>
            <input
              type="file"
              id="imageInput"
              accept="image/*"
              onChange={handleImageChange}
              style={{ display: 'none' }}
            />
          </div>

          <div className={styles.information}>
            <input
              type="text"
              placeholder="名前"
              className={styles.input}
              style={inputStyle}
            />
            <textarea
              placeholder="フリーコメント"
              className={styles.textarea}
              style={inputStyle}
            ></textarea>
          </div>
        </div>
        <button
          className={styles['ok-btn']}
          onClick={handlepetcon}
          style={inputStyle}
        >
          変更
        </button>
      </div>

      <div className={styles.right}>
        <div className={styles.advertisement2}>
          <img
            src={Right1Img} // 広告サンプル
            alt="Right1Img" // 代替テキスト
          />  
        </div>
      </div>
    </div>
    </div>
  );
};

export default PetChange;
